definition.script:
pipeline {
    agent any

    stages {
        stage('Test') {
            environment {
              GITHUB_API_ACCESS_TOKEN = credentials('SapMachine-Github-Token')
            }
            steps {
                sh 'rm -rf infrastructure'
                sh 'git clone https://github.com/SAP/SapMachine-infrastructure.git infrastructure'
                sh 'export SAPMACHINE_TAGS=$(python infrastructure/lib/list_tags.py -m 10)'
                script {
                    def input = System.getenv("SAPMACHINE_TAGS")
                    def list = input.tokenize()

                    list.each { item ->
                        echo "Hello ${item}"
                    }
                }
            }
        }
    }
     post {
        always {
            cleanWs()
        }
    }
}
