definition.script:
pipeline {
    agent any
    stages {
        stage('Copy Artifacts') {
            steps {
                script {
                    def job_linux_x86_64 = build job: 'branch-build_linux_x86_64', propagate: true, wait: true, parameters:
                        [
                            string(name: 'SAPMACHINE_GIT_REPOSITORY', value: 'https://github.com/SAP/SapMachine.git'),
                            string(name: 'SAPMACHINE_GIT_BRANCH', value: 'sapmachine'),
                            string(name: 'GIT_TAG_NAME', value: ''),
                            string(name: 'EXTRA_CONFIGURE_OPTIONS', value: ''),
                            [$class: 'BooleanParameterValue', name: 'RUN_TESTS', value: false],
                            [$class: 'BooleanParameterValue', name: 'PUBLISH', value: false],
                            [$class: 'BooleanParameterValue', name: 'PROPAGATE_RESULT', value: true],
                            [$class: 'BooleanParameterValue', name: 'RELEASE', value: false]
                        ]

                    env.LINUX_X86_64_JOB_ID = Integer.toString(job_linux_x86_64.getNumber())
                }            
            }
            post {
                success {
                    copyArtifacts filter: 'sapmachine-*.tar.gz', projectName: 'branch-build_linux_x86_64', selector: 
                    [
                        $class: 'SpecificBuildSelector',
                        buildNumber: env.LINUX_X86_64_JOB_ID
                    ]

                    archiveArtifacts 'sapmachine-*.tar.gz'
                }
            }
        }
    }
    post {
        always {
            cleanWs deleteDirs: true, disableDeferredWipeout: true
        }
    }
}
