definition.script:
pipeline {
    agent any
    stages {
        stage("Get Plugins") {
            steps {
                script {
                Jenkins.instance.pluginManager.plugins.sort { it.getDisplayName() }.each{
                    plugin -> 
                        println ("${plugin.getDisplayName()} (${plugin.getShortName()}): ${plugin.getVersion()}")
                    }
                }
            }
        }
    }
}
